class Gallery extends BaseGallery
{

    /**
     * @var integer $id
     */
    protected $id;

    /**
     * Get id
     *
     * @return integer $id
     */
    public function getId()
    {
        return $this->id;
    }

    public function getImages($limit = 0)
    {
        if ($limit) {
//            AppDebug::_dx(get_class($this->galleryHasMedias->getValues()));
            return array_slice($this->galleryHasMedias->getValues(), 0, $limit);
        } else {
            return $this->galleryHasMedias;
        }
    }

    public function getImages4()
    {
        return $this->getImages(4);
    }
}